void GUI_Style::InitDefault(bool forceInit)
{
	if(m_Initialized && !forceInit)
		return;

	for(int i = 0; i < ICON_LAST; i++)
		m_DefaultIcon[i] = NULL;

	InitDefaultFont(NULL, NULL);
	InitDefaultIcons();
	InitDefaultColors();

	m_Initialized = true;
}
	
