void GUI_Style::MenuItemSeparator(GUI_MenuItem* item)
{
	float seperatorMarginHorizontal = 5.0f;

	drgBBox bbox;
	bbox.minv.x = item->PosXView() + seperatorMarginHorizontal;
	bbox.maxv.x = bbox.minv.x + item->WidthView() - (seperatorMarginHorizontal * 2);
	if(m_DefaultIcon[TEXTURE_MENU_SEPARATOR_HORIZONTAL] != NULL)
	{
		bbox.minv.y = item->PosYView();
		bbox.maxv.y = bbox.minv.y + item->HeightView();
		m_DrawInfo->DrawRect(item, bbox, &m_DefaultColors[MENU_SEP1_COLOR], m_DefaultIcon[TEXTURE_MENU_SEPARATOR_HORIZONTAL]);
	}
	else
	{
		bbox.minv.y = item->PosYView();
		bbox.maxv.y = bbox.minv.y + (item->HeightView()*0.5f);
		m_DrawInfo->DrawBox(item, bbox, 1, &m_DefaultColors[MENU_SEP2_COLOR]);
		bbox.minv.y = bbox.maxv.y;
		bbox.maxv.y += (item->HeightView()*0.5f);
		m_DrawInfo->DrawBox(item, bbox, 1, &m_DefaultColors[MENU_SEP1_COLOR]);
	}
}

void GUI_Style::DropListBackground(GUI_DropList* list, GUI_DropListItem* selected)
{
	drgBBox bbox;
	bbox.minv.x = list->PosXView();
	bbox.minv.y = list->PosYView();
	bbox.maxv.x = bbox.minv.x + DRG_MIN(list->WidthView(), list->GetParent() == NULL ? FLT_MAX : list->GetParent()->WidthView());
	bbox.maxv.y = bbox.minv.y + list->HeightView();
	if (list->IsDisabled())
		m_DrawInfo->DrawBox(list, bbox, -1, &m_DefaultColors[DISABLED_COLOR]);
	else
		m_DrawInfo->DrawBox(list, bbox, -1, &m_DefaultColors[TLIST_BG_COLOR]);
	bbox.minv.x += (m_DefaultFont->GetFontSize()*0.2f);
	if(selected!=NULL)
		m_DrawInfo->DrawText(list, m_DefaultFont, selected->GetText()->str(), bbox, &m_DefaultColors[TEXTBOX_TXT_COLOR]);
	bbox.minv.x = bbox.maxv.x - (m_DefaultFont->GetFontSize()*1.2f);
	bbox.maxv.x -= (m_DefaultFont->GetFontSize()*0.2f);
	bbox.minv.y += (m_DefaultFont->GetFontSize()*0.2f);
	bbox.maxv.y -= (m_DefaultFont->GetFontSize()*0.2f);
	if(list->IsDroped())
		m_DrawInfo->DrawRect(list, bbox, &m_DefaultColors[DISABLED_COLOR], m_DefaultIcon[ICON_ARRW_DOWN_DOWN]);
	else
		m_DrawInfo->DrawRect(list, bbox, &m_DefaultColors[DISABLED_COLOR], list->IsHovered()?m_DefaultIcon[ICON_ARRW_DOWN_UP]:m_DefaultIcon[ICON_ARRW_DOWN]);
}

void GUI_Style::CollapseButtonIcon(drgBBox bbox, bool collapsed, bool hover)
{
	drgTexture* icon = NULL;
	if(collapsed) {
		if (hover) {
			icon = GetDefaultIcon(ICON_ARRW_RIGHT_DOWN);
		}
		else {
			icon = GetDefaultIcon(ICON_ARRW_RIGHT);
		}
	}
	else {		
		if (hover) {
			icon = GetDefaultIcon(ICON_ARRW_DOWN_DOWN);
		}
		else {
			icon = GetDefaultIcon(ICON_ARRW_DOWN);
		}
	}
	drgColor temp_color = GetDefaultColor(ICON_COLOR);
	m_DrawInfo->DrawRectDirect(bbox, &temp_color, icon);
}

void GUI_Style::TextboxBackground(GUI_Input* ibox)
{
	drgBBox bbox;
	bbox.minv.x = ibox->PosXView();
	bbox.minv.y = ibox->PosYView();
	bbox.maxv.x = bbox.minv.x + DRG_MIN(ibox->WidthView(), ibox->GetParent() == NULL ? FLT_MAX : ibox->GetParent()->WidthView());
	bbox.maxv.y = bbox.minv.y + ibox->HeightView();

	bool useTexture = false;
	DEFAULT_COLOR textboxBGColor;
	drgTexture* textboxBGTexture;
	if (ibox->IsDisabled())
	{
		textboxBGTexture = m_DefaultIcon[TEXTURE_TEXTBOX_1LINE_DISABLED];
		textboxBGColor = DISABLED_COLOR;
	}
	else
	{
		textboxBGTexture = ibox->IsFocused() ? m_DefaultIcon[TEXTURE_TEXTBOX_1LINE_FOCUSED] : m_DefaultIcon[TEXTURE_TEXTBOX_1LINE]; 
		textboxBGColor = ibox->IsFocused() ? TEXTBOX_FOCUS_COLOR : TEXTBOX_COLOR;
	}
		
	useTexture = textboxBGTexture != NULL;
	if(useTexture)
	{		
		m_DrawInfo->DrawHorizontallySlicedRect(ibox, bbox, &m_DefaultColors[textboxBGColor], textboxBGTexture);
	}
	else
	{
		bbox.minv.x = ibox->PosXView();
		bbox.minv.y = ibox->PosYView();
		bbox.maxv.x = bbox.minv.x + ibox->WidthView();
		bbox.maxv.y = bbox.minv.y + ibox->HeightView();

		m_DrawInfo->DrawBox(ibox, bbox, -1, &m_DefaultColors[textboxBGColor]);
	}
}

void GUI_Style::TextboxText(GUI_Input* ibox, unsigned short* text, int startpos, int endpos, int offset, int cursr, bool focused, int selectionStart, int selectionEnd)
{
	drgBBox bbox;	

	float textScale = 1.0f;
	const unsigned short* textBoxText = &text[startpos];
	int textLength = endpos - startpos;
	int textWidth = m_DefaultFont->StringSize(textBoxText, textScale, (cursr-startpos));
	float textLeft = ibox->PosXView() + 3.0f + offset;

	//SELECTION HIGHLIGHT

	if(selectionStart > selectionEnd)
	{
		int temp = selectionEnd;
		selectionEnd = selectionStart;
		selectionStart = temp;
	}

	if(focused && selectionStart < selectionEnd && selectionEnd > 0 && selectionStart >= 0 && selectionStart < textLength && selectionEnd <= textLength)
	{
		int selectionLeftMargin = m_DefaultFont->StringSize(textBoxText, textScale, selectionStart);
		int selectionWidth = m_DefaultFont->StringSize(&textBoxText[selectionStart], textScale, selectionEnd - selectionStart);

		bbox.minv.x = textLeft + 2.0f + selectionLeftMargin;
		bbox.maxv.x = bbox.minv.x + selectionWidth;
		bbox.minv.y = ibox->PosYView() + (m_DefaultFont->GetFontSize()*0.1f);
		bbox.maxv.y = bbox.minv.y + (m_DefaultFont->GetFontSize()*1.25f);

		m_DrawInfo->DrawRect(ibox, bbox, &m_DefaultColors[TEXT_HIGHLIGHT_COLOR]);
	}

	//FOCUS

	if(focused)
	{
		m_CursorTimer += drgEngine::GetDeltaTime();
		if (m_CursorTimer<0.4f)
		{
			bbox.minv.x = textLeft + textWidth;
			bbox.maxv.x = bbox.minv.x + 1.0f;
			bbox.minv.y = ibox->PosYView() + (m_DefaultFont->GetFontSize()*0.25f);
			bbox.maxv.y = bbox.minv.y + m_DefaultFont->GetFontSize();
			DEFAULT_COLOR cursorColor = ibox->IsFocused() ? TEXTBOX_CURSOR_FOCUS_COLOR : TEXTBOX_CURSOR_COLOR;
			m_DrawInfo->DrawBox(ibox, bbox, -1, &m_DefaultColors[cursorColor]);
		}
		else
		{
			if (m_CursorTimer>0.8f)
				m_CursorTimer=0.0f;
		}
	}

	//TEXT

	bbox.minv.x = textLeft + 2.0f;
	bbox.maxv.x = bbox.minv.x + ibox->WidthView();
	bbox.minv.y = ibox->PosYView();
	bbox.maxv.y = bbox.minv.y + ibox->HeightView();
	DEFAULT_COLOR textColor = ibox->IsFocused() ? TEXTBOX_TXT_FOCUS_COLOR : TEXTBOX_TXT_COLOR;
	m_DrawInfo->DrawText(ibox, m_DefaultFont, textBoxText, bbox, &m_DefaultColors[textColor], (endpos-startpos));
}

