#define TAB_BUTTON_OFFSET		2.0f
#define TAB_BUTTON_TEXT_OFFSET	8.0f
void GUI_Style::TabBar(GUI_TabContainer* tab)
{
	drgBBox bbox;
	tab->GetTabBarBounds( &bbox );

	m_DrawInfo->DrawRect(tab, bbox, &m_DefaultColors[NOTEBOOK_BG_COLOR]);
	GUI_Pane* pane = (GUI_Pane*)tab->GetLast();

	while(pane)
	{
		tab->GetTabButtonBounds( pane, &bbox );
		bbox.minv.x += TAB_BUTTON_OFFSET;
		DEFAULT_COLOR textColor;

		if(tab->IsPaneSelected( pane ) == true )
		{
			drgColor* selectedTabColor = &m_DefaultColors[NOTEBOOK_SEL_COLOR];
			drgTexture* selectedTabTexture = m_DefaultIcon[TEXTURE_TAB_FOCUSED];
			if(selectedTabTexture != NULL)
				m_DrawInfo->DrawHorizontallySlicedRect(tab, bbox, selectedTabColor, selectedTabTexture);
			else
				m_DrawInfo->DrawRect(tab, bbox, selectedTabColor);

			textColor = NOTEBOOK_TXT_SEL_COLOR;
		}
		else
		{
			drgColor* tabColor = &m_DefaultColors[NOTEBOOK_TAB_COLOR];
			drgTexture* tabTexture = m_DefaultIcon[TEXTURE_TAB_UNFOCUSED];
			if(tabTexture != NULL)
				m_DrawInfo->DrawHorizontallySlicedRect(tab, bbox, tabColor, tabTexture);
			else
				m_DrawInfo->DrawRect(tab, bbox, tabColor);

			textColor = TITLEBAR_CAPTION_COLOR;
		}
		
		bbox.minv.x += TAB_BUTTON_TEXT_OFFSET;
		m_DrawInfo->DrawText(tab, m_DefaultFont, pane->GetName()->str(), bbox, &m_DefaultColors[textColor]);
		pane = (GUI_Pane*)tab->GetPrev(pane);
	}
}

#define RECEIVER_TAB_OFFSET 75.0f
void GUI_Style::ReceieverTab( GUI_TabContainer* tab )
{
	drgBBox bbox;
	tab->GetTabBarBounds( &bbox );

	// Get the bounds of the right-most tab in the container.
	GUI_Pane* pane = (GUI_Pane*)tab->GetFirst();
	if( pane == NULL )
	{
		bbox.minv.x = 0.0f;
	}
	else
	{
		tab->GetTabButtonBounds( pane, &bbox );
	}

	bbox.minv.x = bbox.maxv.x + TAB_BUTTON_OFFSET;
	bbox.maxv.x = bbox.minv.x + RECEIVER_TAB_OFFSET;
	drgColor color = GetDefaultColor(DOCKING_RECEIVER_COLOR);
	m_DrawInfo->DrawRect(tab, bbox, &color);
}

//The border exists outside of the m_View rect
void GUI_Style::Border(GUI_Container* contain)
{
	drgColor* borderColor = &m_DefaultColors[PANE_BORDER_COLOR];

	drgBBox bbox;
	bbox.minv.x = contain->PosXView();
	bbox.minv.y = contain->PosYView();
	bbox.maxv.x = bbox.minv.x + contain->WidthView();
	bbox.maxv.y = bbox.minv.y + contain->HeightView();

	drgBBox borderBBox;

	borderBBox.minv.x = bbox.minv.x - m_BorderWidth;
	borderBBox.minv.y = bbox.minv.y;
	borderBBox.maxv.x = bbox.minv.x;
	borderBBox.maxv.y = bbox.maxv.y;
	m_DrawInfo->DrawRect(contain, borderBBox, borderColor, m_DefaultIcon[TEXTURE_BORDER_LEFT]);

	borderBBox.minv.x = bbox.maxv.x;
	borderBBox.minv.y = bbox.minv.y;
	borderBBox.maxv.x = bbox.maxv.x + m_BorderWidth;
	borderBBox.maxv.y = bbox.maxv.y;
	m_DrawInfo->DrawRect(contain, borderBBox, borderColor, m_DefaultIcon[TEXTURE_BORDER_RIGHT]);

	borderBBox.minv.x = bbox.minv.x;
	borderBBox.minv.y = bbox.minv.y - m_BorderWidth;
	borderBBox.maxv.x = bbox.maxv.x;
	borderBBox.maxv.y = bbox.minv.y;
	m_DrawInfo->DrawHorizontallySlicedRect(contain, borderBBox, borderColor, m_DefaultIcon[TEXTURE_BORDER_TOP]);

	borderBBox.minv.x = bbox.minv.x;
	borderBBox.minv.y = bbox.maxv.y;
	borderBBox.maxv.x = bbox.maxv.x;
	borderBBox.maxv.y = bbox.maxv.y + m_BorderWidth;
	m_DrawInfo->DrawHorizontallySlicedRect(contain, borderBBox, borderColor, m_DefaultIcon[TEXTURE_BORDER_BOTTOM]);
}

