void GUI_Style::ViewBox(GUI_Widget* widget, drgColor* col)
{
	drgBBox bbox;
	bbox.minv.x = widget->PosXView();
	bbox.minv.y = widget->PosYView();
	bbox.maxv.x = bbox.minv.x + widget->WidthView();
	bbox.maxv.y = bbox.minv.y + widget->HeightView();
	m_DrawInfo->DrawBox(widget, bbox, 1, col);
}

void GUI_Style::SensBox(GUI_Widget* widget, drgColor* col)
{
	drgBBox bbox;
	bbox.minv.x = widget->PosXBox();
	bbox.minv.y = widget->PosYBox();
	bbox.maxv.x = bbox.minv.x + widget->WidthBox();
	bbox.maxv.y = bbox.minv.y + widget->HeightBox();
	m_DrawInfo->DrawBox(widget, bbox, 1, col);
}



